CREATE TABLE image (id_image INTEGER AUTO_INCREMENT NOT NULL, Image_01 LONGTEXT, Image_02 LONGTEXT, Image_03 LONGTEXT, Image_04 LONGTEXT, Image_05 LONGTEXT, Nome_image VARCHAR(255), PRIMARY KEY (id_image))
CREATE TABLE tb_administrador (id_Administrador INTEGER AUTO_INCREMENT NOT NULL, email_Administrador VARCHAR(255), login_Administrador VARCHAR(255), nome_Administrador VARCHAR(255), senha_Administrador VARCHAR(255), PRIMARY KEY (id_Administrador))
CREATE TABLE tb_promocao (ID_Promocao INTEGER AUTO_INCREMENT NOT NULL, DataFim DATE, DataInicio DATE, DescontoPreco FLOAT, PRIMARY KEY (ID_Promocao))
CREATE TABLE tb_pedido (ID_Pedido INTEGER AUTO_INCREMENT NOT NULL, Arquivo_pedido LONGTEXT, Cor VARCHAR(255), Mensagem VARCHAR(255), Quantidade_Pedido INTEGER, Status_Pedido VARCHAR(255), Cartão_id INTEGER, id_usu INTEGER, PRIMARY KEY (ID_Pedido))
CREATE TABLE tb_cartaodecredito (ID_Cartao INTEGER AUTO_INCREMENT NOT NULL, Bandeira VARCHAR(255), CodigoSeguranca INTEGER, NomeTitular VARCHAR(255), Numero INTEGER, Validade DATE, PRIMARY KEY (ID_Cartao))
CREATE TABLE tb_produto (ID_Produto INTEGER AUTO_INCREMENT NOT NULL, Cor LONGTEXT, Descricao VARCHAR(255), Nome VARCHAR(255), Preco FLOAT, Quantidade INTEGER, Tamanho_Altura FLOAT, Tamanho_Largura FLOAT, tipo VARCHAR(255), Produto_Promocao INTEGER, PRIMARY KEY (ID_Produto))
CREATE TABLE tb_cliente (User_ID INTEGER AUTO_INCREMENT NOT NULL, bairro_usuario VARCHAR(255), celular_usuario INTEGER, cep_usuario INTEGER, cidade_usuario VARCHAR(255), Email VARCHAR(255), Login VARCHAR(255), Nome VARCHAR(255), Senha VARCHAR(255), sobrenome_usuario VARCHAR(255), telefone_usuario INTEGER, Cartao INTEGER, PRIMARY KEY (User_ID))
ALTER TABLE image ADD CONSTRAINT FK_image_id_image FOREIGN KEY (id_image) REFERENCES tb_produto (ID_Produto)
ALTER TABLE tb_administrador ADD CONSTRAINT FK_tb_administrador_id_Administrador FOREIGN KEY (id_Administrador) REFERENCES tb_produto (ID_Produto)
ALTER TABLE tb_pedido ADD CONSTRAINT FK_tb_pedido_Cartão_id FOREIGN KEY (Cartão_id) REFERENCES tb_cartaodecredito (ID_Cartao)
ALTER TABLE tb_pedido ADD CONSTRAINT FK_tb_pedido_ID_Pedido FOREIGN KEY (ID_Pedido) REFERENCES tb_produto (ID_Produto)
ALTER TABLE tb_pedido ADD CONSTRAINT FK_tb_pedido_id_usu FOREIGN KEY (id_usu) REFERENCES tb_cliente (User_ID)
ALTER TABLE tb_produto ADD CONSTRAINT FK_tb_produto_Produto_Promocao FOREIGN KEY (Produto_Promocao) REFERENCES tb_promocao (ID_Promocao)
ALTER TABLE tb_cliente ADD CONSTRAINT FK_tb_cliente_Cartao FOREIGN KEY (Cartao) REFERENCES tb_cartaodecredito (ID_Cartao)
